mapper.select:
SELECT t_log.log_id, t_log.log_type, t_log.content, t_log.log_date, t_log.user_id FROM t_log LEFT JOIN t_user ON t_log.user_id = t_user.user_id
AND t_log.log_type = #{logType}
AND t_user.true_name LIKE CONCAT('%', #{trueName} ,'%')
AND t_log.log_date >= #{sTime}
AND t_log.log_date <= #{eTime}
ORDER BY t_log.log_date DESC LIMIT #{offSet}, #{pageRow}

SELECT
  COUNT(*)
FROM
  t_log
LEFT JOIN t_user ON t_log.user_id = t_user.user_id


        AND t_log.log_type = #{logType}


        AND t_user.true_name LIKE CONCAT('%', #{trueName} ,'%')


        AND t_log.log_date >= #{sTime}


        AND t_log.log_date <= #{eTime}
